// App.js
import React, { useState } from 'react';
import { ChakraProvider, Box, Heading, VStack, Button, Text, Modal, ModalOverlay, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton } from '@chakra-ui/react';
import customTheme from './theme'; // Import custom Chakra UI theme
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';

function App() {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [todoToDelete, setTodoToDelete] = useState(null);

  const addTodo = (text) => {
    const newTodo = { id: Date.now(), text };
    setTodos([...todos, newTodo]);
    setNewTodo('');
  };

  const handleCheck = (id) => {
    const completedTodo = todos.find(todo => todo.id === id);
    if (completedTodo) {
      setTodos(todos.filter(todo => todo.id !== id));
      setCompletedTodos([...completedTodos, completedTodo]);
    }
  };

  const handleDelete = (id) => {
    setIsOpen(true);
    setTodoToDelete(id);
  };

  const confirmDelete = () => {
    const todoToDeleteObj = todos.find(todo => todo.id === todoToDelete);
    setIsOpen(false);
    if (todoToDeleteObj) {
      setTodos(todos.filter(todo => todo.id !== todoToDelete));
    } else {
      const completedTodoToDeleteObj = completedTodos.find(todo => todo.id === todoToDelete);
      setCompletedTodos(completedTodos.filter(todo => todo.id !== todoToDelete));
    }
  };

  const handleDeleteCompleted = () => {
    setCompletedTodos([]);
  };

  return (
    <ChakraProvider theme={customTheme}>
      <Box
        display="flex"
        flexDirection="column"
        p={10} // Increased padding
        maxW="md"
        mx="auto"
        mt={8}
        bg="#252525"
        color="#FFF5DA" // Unified text color
      >
        <Heading
          mb={4}
          as="h1"
          size="2xl"
          textAlign="left"
          className="bigmarker-cg-keynote-title"
          style={{
            fontSize: '3rem',
            color: '#FFF5DA',
            letterSpacing: '-0.48px',
            fontWeight: 350,
            lineHeight: '1.15em',
          }}
        >
          <span
            style={{
              display: 'inline',
              background: 'linear-gradient(to left, #FFF5DA, #FFF5DA)',
              backgroundPosition: '0% 0%',
              backgroundRepeat: 'repeat',
              backgroundSize: '0 2px',
              textDecoration: 'underline solid rgba(255, 245, 218, 1)',
              textDecorationThickness: '2px',
              textUnderlineOffset: '2px',
              transition: 'background-size 400ms cubic-bezier(0.8, 0, 0.2, 1), text-decoration-color 400ms cubic-bezier(0.8, 0, 0.2, 1)',
              lineHeight: '1.15em',
            }}
          >
            Todapp App
          </span>
        </Heading>

        <TodoInput addTodo={addTodo} newTodo={newTodo} setNewTodo={setNewTodo} />

        <VStack spacing={2} width="auto" mx="auto">
          {todos.map((todo) => (
            <Box key={todo.id} width="414px">
              <TodoItem todo={todo} onDelete={handleDelete} onCheck={handleCheck} />
            </Box>
          ))}
        </VStack>

        {completedTodos.length > 0 && (
          <Box mt={8} width="414px" mx="auto" borderWidth={1} borderRadius="md">
            <Heading size="md" mb={4}>
              Completed Todos
            </Heading>
            {completedTodos.map((todo) => (
              <Box
                key={todo.id}
                display="flex"
                alignItems="center"
                justifyContent="space-between"
                p={2}
                borderWidth={1}
                borderRadius="md"
                mb={2}
              >
                <Text>{todo.text}</Text>
              </Box>
            ))}
            <Button
              mt={4}
              colorScheme="red"
              onClick={handleDeleteCompleted}
            >
              Delete All Completed
            </Button>
          </Box>
        )}
      </Box>

      <Modal isOpen={isOpen} onClose={() => setIsOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Confirm Delete</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            Are you sure you want to delete this todo?
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="red" mr={3} onClick={confirmDelete}>
              Delete
            </Button>
            <Button variant="ghost" onClick={() => setIsOpen(false)}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </ChakraProvider>
  );
}

export default App;
