import React from 'react';
import { Box, Text, Button } from '@chakra-ui/react';

const ColorOverview = ({ themeColors }) => {
  const allColors = { ...themeColors }; // Combine all color categories

  // Function to determine text color based on background color brightness
  const getTextColor = (bgColor) => {
    const rgb = hexToRgb(bgColor);
    if (!rgb) return 'black'; // Default to black if unable to determine brightness
    const brightness = rgb[0] * 0.299 + rgb[1] * 0.587 + rgb[2] * 0.114;
    return brightness < 128 ? 'white' : 'black'; // Use white text for dark backgrounds, black for light backgrounds
  };

  // Function to convert hex color to RGB array
  const hexToRgb = (hex) => {
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b);
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? [parseInt(result[1], 16), parseInt(result[2], 16), parseInt(result[3], 16)] : null;
  };

  // Function to copy color code to clipboard
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  return (
    <Box>
      {Object.keys(allColors).map((colorCategory) => (
        <Box key={colorCategory} mb={4}>
          <Text fontSize="lg" fontWeight="bold" mb={2} color="brand.800">
            {colorCategory}
          </Text>
          {Object.keys(allColors[colorCategory]).map((colorName) => (
            <Box key={colorName} display="flex" alignItems="center" mb={2}>
              <Box w="30px" h="30px" mr={2} bg={allColors[colorCategory][colorName]} borderRadius="md" border="1px solid #ccc" />
              <Text color={getTextColor(allColors[colorCategory][colorName])} mr={2}>{colorName}</Text>
              <Text color="brand.800">{allColors[colorCategory][colorName]}</Text>
              <Button ml={2} size="sm" colorScheme="brand" onClick={() => copyToClipboard(allColors[colorCategory][colorName])}>
                Copy
              </Button>
            </Box>
          ))}
        </Box>
      ))}
    </Box>
  );
};

export default ColorOverview;
