import React from 'react';
import { Box, Text, VStack, HStack, IconButton, useClipboard, Tooltip } from '@chakra-ui/react';

const ColorOverview = ({ themeColors }) => {
  const { hasCopied, onCopy } = useClipboard();

  return (
    <Box p={6} bg="brand.800" color="brand.50" mt={20}>
      <VStack align="stretch" spacing={4}>
        {Object.entries(themeColors).map(([key, value]) => (
          <HStack key={key} spacing={4} alignItems="center">
            <Box bg={value} w="50px" h="50px" borderRadius="md" border="1px solid" borderColor={key.startsWith('brand') ? 'brand.500' : 'transparent'} />
            <VStack align="stretch" spacing={1}>
              <Text fontSize="md" fontWeight="bold">{key}</Text>
              <Box bg={value} p={2} borderRadius="md">
                <Tooltip label={hasCopied ? 'Copied!' : 'Copy Hex'} placement="top">
                  <IconButton
                    aria-label="Copy color code"
                    icon="copy"
                    onClick={() => {
                      onCopy(value);
                    }}
                  />
                </Tooltip>
                <Text fontSize="sm" color="brand.50">{value}</Text>
              </Box>
            </VStack>
          </HStack>
        ))}
      </VStack>
    </Box>
  );
};

export default ColorOverview;
