import React, { useState } from 'react';
import { ChakraProvider, Box, Heading, VStack, Button, Text, Modal, ModalOverlay, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton } from '@chakra-ui/react';
import { ColorModeScript } from '@chakra-ui/react'; // Import ColorModeScript
import customTheme from './theme'; // Import custom Chakra UI theme
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';
import ColorOverview from './ColorOverview'; // Import ColorOverview component

function App() {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [todoToDelete, setTodoToDelete] = useState(null);

  const addTodo = (text) => {
    const newTodo = { id: Date.now(), text };
    setTodos([...todos, newTodo]);
    setNewTodo('');
  };

  const completeTodo = (id) => {
    const completedTodo = todos.find(todo => todo.id === id);
    if (completedTodo) {
      setTodos(todos.filter(todo => todo.id !== id));
      setCompletedTodos([...completedTodos, completedTodo]);
    }
  };

  const deleteTodo = (id) => {
    setIsOpen(true);
    setTodoToDelete(id);
  };

  const confirmDelete = () => {
    const todoToDeleteObj = todos.find(todo => todo.id === todoToDelete);
    setIsOpen(false);
    if (todoToDeleteObj) {
      setTodos(todos.filter(todo => todo.id !== todoToDelete));
    } else {
      const completedTodoToDeleteObj = completedTodos.find(todo => todo.id === todoToDelete);
      setCompletedTodos(completedTodos.filter(todo => todo.id !== todoToDelete));
    }
  };

  const deleteCompletedTodos = () => {
    setCompletedTodos([]);
  };

  return (
    <ChakraProvider theme={customTheme}>
      <ColorModeScript initialColorMode={customTheme.config.initialColorMode} />
      <Box p={10} maxW="500px" mx="auto" mt={8} bg="brand.800" color="brand.50" borderRadius="md" boxShadow="lg" mb={6}>
        <Heading mb={4} as="h1" size="2xl" textAlign="left">
          Todapp App
        </Heading>

        <TodoInput addTodo={addTodo} newTodo={newTodo} setNewTodo={setNewTodo} />

        <VStack spacing={4} width="100%" align="stretch">
          {todos.map((todo) => (
            <Box key={todo.id} p={2} bg="brand.700" color="brand.50" borderColor="brand.500" borderRadius="md">
              <TodoItem todo={todo} onCheck={completeTodo} onDelete={deleteTodo} />
            </Box>
          ))}
        </VStack>

        {completedTodos.length > 0 && (
          <Box mt={6} borderTopWidth={1} borderTopColor="brand.50" pt={4}>
            <Heading size="md" mb={4} color="brand.50">
              Completed Todos
            </Heading>
            <VStack align="stretch" spacing={4}>
              {completedTodos.map((todo) => (
                <Box key={todo.id} p={2} bg="brand.700" borderRadius="md">
                  <Text color="brand.50" textDecoration="line-through">{todo.text}</Text>
                </Box>
              ))}
            </VStack>
            <Button mt={4} colorScheme="red" onClick={deleteCompletedTodos}>
              Delete All Completed
            </Button>
          </Box>
        )}
      </Box>

      {/* ColorOverview Component */}
      <Box p={10} maxW="500px" mx="auto" bg="brand.800" color="brand.50" borderRadius="md" boxShadow="lg" mb={6}>
        <ColorOverview themeColors={customTheme.colors} />
      </Box>

      <Modal isOpen={isOpen} onClose={() => setIsOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Confirm Delete</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            Are you sure you want to delete this todo?
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="red" mr={3} onClick={confirmDelete}>
              Delete
            </Button>
            <Button variant="ghost" onClick={() => setIsOpen(false)}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </ChakraProvider>
  );
}

export default App;
