// src/theme.js
import { extendTheme } from '@chakra-ui/react';

const customTheme = extendTheme({
  colors: {
    brand: {
      50: '#fff5da', // light color
      100: '#f8f9fc',
      200: '#f1f3f9',
      300: '#dee3ed',
      400: '#c2c9d6',
      500: '#8f96a3', // primary color for buttons
      600: '#5e636e',
      700: '#2f3237',
      800: '#1d1e20', // dark color
      900: '#111213',
    },
  },
  fonts: {
    heading: 'Inter, sans-serif',
    body: 'Inter, sans-serif',
  },
  styles: {
    global: {
      'html, body': {
        backgroundColor: '#FFF5DA',
        color: 'black', // optional: set the default text color to ensure readability
      },
      'input, textarea, select': {
        bg: 'brand.700', // Dark background for text inputs
        color: 'brand.50', // Light text color for text inputs
        _hover: {
          bg: 'brand.800', // Darken on hover
        },
        _focus: {
          bg: 'brand.700', // Maintain dark background on focus
        },
      },
      'ul, ol': {
        bg: 'brand.700', // Dark background for list elements
        color: 'brand.50', // Light text color for list elements
        listStyleType: 'none', // Optional: Remove default list styles
        paddingInlineStart: 0, // Optional: Remove default list padding
      },
    },
  },
  components: {
    Button: {
      baseStyle: {
        borderRadius: '9999px', // complete half-circle on each end
      },
      variants: {
        solid: {
          bg: 'brand.50',
          color: 'brand.800',
          _hover: {
            bg: 'brand.100',
            color: 'brand.800',
          },
        },
        outline: {
          borderColor: 'brand.500',
          color: 'brand.500',
          _hover: {
            bg: 'brand.500',
            color: 'brand.800',
          },
        },
        ghost: {
          color: 'brand.500',
          _hover: {
            bg: 'brand.500',
            color: 'brand.800',
          },
        },
      },
    },
    Input: {
      variants: {
        filled: {
          field: {
            bg: 'brand.800', // Dark background color
            color: 'brand.50', // Light text color
            _hover: {
              bg: 'brand.900', // Darken on hover
            },
            _focus: {
              bg: 'brand.800', // Maintain dark background on focus
            },
          },
        },
      },
    },
    Box: {
      baseStyle: {
        bg: 'brand.800',
        color: 'brand.50',
        borderWidth: 1,
        borderColor: 'brand.500',
        borderRadius: 'md',
        p: 2,
      },
    },
    Text: {
      baseStyle: {
        color: 'brand.50',
      },
    },
  },
});

export default customTheme;
