import React, { useState } from 'react';
import { ChakraProvider, Box, Heading, VStack, Button, Text } from '@chakra-ui/react';
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';

import customTheme from './theme'; // Import your custom theme

function App() {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);

  const addTodo = (text) => {
    const newTodo = {
      id: todos.length + 1,
      text: text,
      completed: false,
    };
    setTodos([...todos, newTodo]);
  };

  const completeTodo = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, completed: true } : todo
    );
    const completedTodo = todos.find((todo) => todo.id === id);
    setCompletedTodos([...completedTodos, completedTodo]);
    setTodos(updatedTodos.filter((todo) => todo.id !== id));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const deleteCompletedTodos = () => {
    setCompletedTodos([]);
  };

  return (
    <ChakraProvider theme={customTheme}>
      <Box p={10} maxW="md" mx="auto" mt={8} bg="#252525" color="#FFF5DA">
        <Heading
          mb={4}
          as="h1"
          size="2xl"
          textAlign="left"
          className="bigmarker-cg-keynote-title"
          style={{
            fontSize: '3rem',
            color: '#FFF5DA',
            letterSpacing: '-0.48px',
            fontWeight: 350,
            lineHeight: '1.15em',
          }}
        >
          Todapp App
        </Heading>

        <TodoInput addTodo={addTodo} />

        <VStack spacing={4} width="100%" align="stretch">
          {todos.map((todo) => (
            <TodoItem key={todo.id} todo={todo} onCheck={completeTodo} onDelete={deleteTodo} />
          ))}
        </VStack>

        {completedTodos.length > 0 && (
          <Box mt={20} borderTopWidth={1} borderTopColor="brand.50" pt={4}>
            <Heading size="md" mb={4} color="brand.50">
              Completed Todos
            </Heading>
            <VStack align="stretch" spacing={4}>
              {completedTodos.map((todo) => (
                <Box key={todo.id} p={2} bg="brand.700" borderRadius="md">
                  <Text color="brand.50" textDecoration="line-through">
                    {todo.text}
                  </Text>
                </Box>
              ))}
            </VStack>
            <Button mt={4} colorScheme="red" onClick={deleteCompletedTodos}>
              Delete All Completed
            </Button>
          </Box>
        )}
      </Box>
    </ChakraProvider>
  );
}

export default App;
