import React, { useState } from 'react';
import { ChakraProvider, Box, Heading, VStack, Button } from '@chakra-ui/react';
import customTheme from './theme'; // Import custom Chakra UI theme
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';

function App() {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [todoToDelete, setTodoToDelete] = useState(null);

  const addTodo = (text) => {
    const newTodo = { id: Date.now(), text };
    setTodos([...todos, newTodo]);
    setNewTodo('');
  };

  const handleCheck = (id) => {
    const completedTodo = todos.find(todo => todo.id === id);
    if (completedTodo) {
      setTodos(todos.filter(todo => todo.id !== id));
      setCompletedTodos([...completedTodos, completedTodo]);
    }
  };

  const handleDelete = (id) => {
    setIsOpen(true);
    setTodoToDelete(id);
  };

  const confirmDelete = () => {
    const todoToDeleteObj = todos.find(todo => todo.id === todoToDelete);
    setIsOpen(false);
    if (todoToDeleteObj) {
      setTodos(todos.filter(todo => todo.id !== todoToDelete));
    } else {
      const completedTodoToDeleteObj = completedTodos.find(todo => todo.id === todoToDelete);
      setCompletedTodos(completedTodos.filter(todo => todo.id !== todoToDelete));
    }
  };

  const handleDeleteCompleted = () => {
    setCompletedTodos([]);
  };

  return (
    <ChakraProvider theme={customTheme}>
      <Box p={4} maxW="md" mx="auto" mt={8}>
        {/* Headline styled as per your requirements */}
        <Heading
          mb={4}
          as="h1"
          size="2xl"
          textAlign="left"
          className="bigmarker-cg-keynote-title"
          style={{
            fontSize: '3rem',
            color: '#FFF5DA',
            letterSpacing: '-0.48px',
            fontWeight: 350,
            lineHeight: '1.15em',
          }}
        >
          <span
            style={{
              display: 'inline',
              background: 'linear-gradient(to left, #FFF5DA, #FFF5DA)',
              backgroundPositionX: '0%',
              backgroundPositionY: '0%',
              backgroundRepeat: 'repeat',
              backgroundSize: '0 2px',
              textDecoration: 'underline',
              textDecorationStyle: 'solid',
              textDecorationColor: 'rgba(255, 245, 218, 1)',
              textDecorationThickness: '2px',
              textUnderlineOffset: '2px',
              transition: 'background-size cubic-bezier(0.8, 0, 0.2, 1) 400ms, text-decoration-color cubic-bezier(0.8, 0, 0.2, 1) 400ms',
              lineHeight: '1.15em',
            }}
          >
            Todapp App
          </span>
        </Heading>

        {/* TodoInput Component for adding todos */}
        <TodoInput addTodo={addTodo} newTodo={newTodo} setNewTodo={setNewTodo} />

        {/* TodoList to display todos */}
        <VStack spacing={4}>
          {todos.map((todo) => (
            <TodoItem key={todo.id} todo={todo} onDelete={handleDelete} onCheck={handleCheck} />
          ))}
        </VStack>
      </Box>

      {/* Modal for delete confirmation */}
      <Modal isOpen={isOpen} onClose={() => setIsOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Confirm Delete</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            Are you sure you want to delete this todo?
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="blue" mr={3} onClick={confirmDelete}>
              Delete
            </Button>
            <Button variant="ghost" onClick={() => setIsOpen(false)}>Cancel</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Display completed todos */}
      {completedTodos.length > 0 && (
        <Box mt={8} p={4} maxW="md" mx="auto" borderWidth={1} borderRadius="md">
          <Heading size="md" mb={4}>
            Completed Todos
          </Heading>
          {completedTodos.map((todo) => (
            <Box key={todo.id} display="flex" alignItems="center" justifyContent="space-between" p={2} bg="gray.200" mb={2} borderRadius="md">
              <Text textDecoration="line-through">{todo.text}</Text>
              <Button onClick={() => handleDelete(todo.id)} colorScheme="red" size="sm" ml={2}>
                Delete
              </Button>
            </Box>
          ))}
          <Button onClick={handleDeleteCompleted} colorScheme="red" size="sm" mt={4}>
            Delete All Completed Todos
          </Button>
        </Box>
      )}
    </ChakraProvider>
  );
}

export default App;
