import React from 'react';
import { Box, Text, VStack, HStack, IconButton, Tooltip } from '@chakra-ui/react';
import { CopyIcon } from '@chakra-ui/icons';

const ColorOverview = ({ themeColors }) => {
  const handleCopy = (value) => {
    navigator.clipboard.writeText(value);
  };

  return (
    <Box p={6} bg="brand.800" color="brand.50" mt={20}>
      <VStack align="stretch" spacing={4}>
        {Object.entries(themeColors).map(([key, value]) => (
          <HStack key={key} spacing={4} alignItems="center">
            <Box bg={value} w="50px" h="50px" borderRadius="md" />
            <VStack align="stretch" spacing={1}>
              <Text fontSize="md" fontWeight="bold">{key}</Text>
              <Box bg="brand.700" p={2} borderRadius="md" display="flex" alignItems="center">
                <Text fontSize="sm" color="brand.50">{value}</Text>
                <Tooltip label="Copy Hex" placement="top">
                  <IconButton
                    aria-label="Copy color code"
                    icon={<CopyIcon />}
                    onClick={() => handleCopy(value)}
                    ml={2}
                    size="sm"
                  />
                </Tooltip>
              </Box>
            </VStack>
          </HStack>
        ))}
      </VStack>
    </Box>
  );
};

export default ColorOverview;
