import React from 'react';
import { Box, Text, Button, VStack } from '@chakra-ui/react';

const ColorOverview = ({ themeColors }) => {
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    // Optional: Provide feedback to the user that text was copied
  };

  return (
    <Box p={10} maxW="md" mx="auto" mt={8} bg="brand.800" color="brand.50">
      <VStack align="stretch" spacing={0}>
        {Object.entries(themeColors).map(([key, value]) => (
          <Box key={key} display="flex" alignItems="center" justifyContent="space-between" p={2}>
            <Box flex="1">
              <Text>{key}</Text>
              <Text fontSize="sm" color="gray.500">{value}</Text>
            </Box>
            <Button size="sm" onClick={() => copyToClipboard(value)}>Copy</Button>
          </Box>
        ))}
      </VStack>
    </Box>
  );
};

export default ColorOverview;
