import React from 'react';
import { Input, Button, VStack, useTheme } from '@chakra-ui/react';

function TodoInput({ addTodo, newTodo, setNewTodo }) {
  const theme = useTheme();

  const handleAddTodo = () => {
    if (newTodo.trim() !== '') {
      addTodo(newTodo);
      setNewTodo('');
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      handleAddTodo();
    }
  };

  return (
    <VStack spacing={4} width="100%" align="stretch" pb={4}>
      <Input
        placeholder="Enter a new todo"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        onKeyDown={handleKeyDown}
        variant="filled"
        size="lg"
        bg={theme.colors.brand[800]} // Dark background color
        color={theme.colors.brand[50]} // Light text color
        _hover={{ bg: theme.colors.brand[900] }} // Darken on hover
        _focus={{ bg: theme.colors.brand[800] }} // Maintain dark background on focus
      />
      <Button onClick={handleAddTodo} colorScheme="brand" size="lg">
        Add Todo
      </Button>
    </VStack>
  );
}

export default TodoInput;
