// src/App.js
import React, { useState } from 'react';
import { ChakraProvider, Box, Heading, VStack, Input, Button, Text } from '@chakra-ui/react';
import customTheme from './theme';  // Importiere das benutzerdefinierte Thema

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  const handleAddTodo = () => {
    if (newTodo.trim() !== '') {
      setTodos([...todos, newTodo]);
      setNewTodo('');
    }
  };

  return (
    <ChakraProvider theme={customTheme}>  // Verwende das benutzerdefinierte Thema
      <Box p={4} maxW="md" mx="auto" mt={8}>
        <Heading mb={4} as="h1" size="xl" textAlign="center">
          ToDo App
        </Heading>
        <VStack spacing={4}>
          <Input
            placeholder="Enter a new todo"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
            variant="filled"
            size="lg"
          />
          <Button onClick={handleAddTodo} colorScheme="brand" size="lg">
            Add Todo
          </Button>
          {todos.map((todo, index) => (
            <Box key={index} w="100%" p={2} borderWidth={1} borderRadius="md">
              <Text>{todo}</Text>
            </Box>
          ))}
        </VStack>
      </Box>
    </ChakraProvider>
  );
}

export default App;
