import React from 'react';
import { Text, Flex, Checkbox } from '@chakra-ui/react';

function TodoItem({ todo, completeTodo }) {
  return (
    <Flex
      alignItems="center"
      justifyContent="space-between"
      width="100%"
      py={2}
      px={4}
      borderBottomWidth="1px"
      borderBottomColor="gray.300"
      color={todo.completed ? "gray.500" : "inherit"}  // Use gray color for completed items
      textDecoration={todo.completed ? "line-through" : "none"}  // Strike-through for completed items
    >
      <Text flex="1">{todo.text}</Text>
      <Checkbox isChecked={todo.completed} onChange={() => completeTodo(todo.id)} />
    </Flex>
  );
}

export default TodoItem;
