// src/theme.js
import { extendTheme } from '@chakra-ui/react';

const customTheme = extendTheme({
  colors: {
    brand: {
      50: '#f5f7ff',
      100: '#ebeffe',
      200: '#c4ccfd',
      300: '#9ea9fb',
      400: '#5263f9',
      500: '#051df7',
      600: '#041ac7',
      700: '#031495',
      800: '#020e63',
      900: '#010833',
    },
  },
  fonts: {
    heading: 'Inter, sans-serif',
    body: 'Inter, sans-serif',
  },
  styles: {
    global: {
      body: {
        bg: '#f5f7ff',
        color: '#2D3748',
      },
    },
  },
  components: {
    Button: {
      baseStyle: {
        rounded: 'lg',
      },
      sizes: {
        md: {
          h: 10,
          fontSize: 'md',
          px: 6,
        },
      },
      variants: {
        solid: (props) => ({
          bg: props.colorMode === 'dark' ? 'brand.200' : 'brand.500',
          color: 'white',
        }),
      },
    },
  },
});

export default customTheme;
