import React, { useState } from 'react';
import {
  ChakraProvider,
  Box,
  Heading,
  VStack,
  Text,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  ColorModeScript,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  IconButton,
  Button, // Import Button component from Chakra UI
  useColorMode, // Import useColorMode hook from Chakra UI
} from '@chakra-ui/react';
import { FiMoreVertical } from 'react-icons/fi';
import { SunIcon, MoonIcon } from '@chakra-ui/icons';
import customTheme from './theme'; // Import custom Chakra UI theme
import TodoInput from './TodoInput';
import TodoItem from './TodoItem';
import ColorOverview from './ColorOverview'; // Import ColorOverview component

function App() {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [todoToDelete, setTodoToDelete] = useState(null);
  const { toggleColorMode } = useColorMode(); // Access the toggleColorMode function

  const addTodo = (text) => {
    const newTodo = { id: Date.now(), text };
    setTodos([...todos, newTodo]);
    setNewTodo('');
  };

  const completeTodo = (id) => {
    const completedTodo = todos.find((todo) => todo.id === id);
    setTodos(todos.filter((todo) => todo.id !== id));
    setCompletedTodos([...completedTodos, completedTodo]);
  };

  const deleteTodo = (id) => {
    setIsOpen(true);
    setTodoToDelete(id);
  };

  const confirmDelete = () => {
    const todoToDeleteObj = todos.find((todo) => todo.id === todoToDelete);
    setIsOpen(false);
    if (todoToDeleteObj) {
      setTodos(todos.filter((todo) => todo.id !== todoToDelete));
    } else {
      const completedTodoToDeleteObj = completedTodos.find((todo) => todo.id === todoToDelete);
      setCompletedTodos(completedTodos.filter((todo) => todo.id !== todoToDelete));
    }
  };

  const deleteCompletedTodos = () => {
    setCompletedTodos([]);
  };

  const CompletedTodos = ({ completedTodos, onDeleteCompleted }) => {
    return (
      <Box mt={8}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
          <Heading as="h2" size="lg" color="brand.50">
            Completed Todos
          </Heading>
          <Menu>
            <MenuButton as={IconButton} icon={<FiMoreVertical />} variant="outline" aria-label="Options" color="brand.50" />
            <MenuList>
              <MenuItem onClick={onDeleteCompleted}>Delete All Completed Todos</MenuItem>
            </MenuList>
          </Menu>
        </Box>
        <Box>
          {completedTodos.map((todo) => (
            <Box key={todo.id} p={4} bg="brand.700" borderRadius="md" mb={2}>
              <Text color="brand.50" textDecoration="line-through">
                {todo.text}
              </Text>
            </Box>
          ))}
        </Box>
      </Box>
    );
  };

  return (
    <ChakraProvider theme={customTheme}>
      <ColorModeScript initialColorMode={customTheme.config.initialColorMode} />
      <Box p={10} maxW="md" mx="auto" mt={8} bg="brand.800" color="brand.50">
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
          <Heading mb={16} as="h1" size="2xl" textAlign="left" className="bigmarker-cg-keynote-title">
            <span
              style={{
                textDecoration: 'underline solid rgba(255, 245, 218, 1)',
                textDecorationThickness: '2px',
                textUnderlineOffset: '2px',
                transition: 'background-size 400ms cubic-bezier(0.8, 0, 0.2, 1), text-decoration-color 400ms cubic-bezier(0.8, 0, 0.2, 1)',
              }}
            >
              Todapp App
            </span>
          </Heading>
          <IconButton
            aria-label="Toggle dark mode"
            icon={customTheme.config.initialColorMode === 'dark' ? <SunIcon /> : <MoonIcon />}
            onClick={toggleColorMode}
            variant="ghost"
          />
        </Box>

        <TodoInput addTodo={addTodo} newTodo={newTodo} setNewTodo={setNewTodo} />

        <VStack spacing={4} width="100%" align="stretch">
          {todos.map((todo) => (
            <Box key={todo.id} p={2} bg="brand.700" color="brand.50" borderColor="brand.500" borderRadius="md">
              <TodoItem todo={todo} onCheck={completeTodo} onDelete={deleteTodo} />
            </Box>
          ))}
        </VStack>

        {completedTodos.length > 0 && <CompletedTodos completedTodos={completedTodos} onDeleteCompleted={deleteCompletedTodos} />}
      </Box>

      <Modal isOpen={isOpen} onClose={() => setIsOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Confirm Delete</ModalHeader>
          <ModalCloseButton />
          <ModalBody>Are you sure you want to delete this todo?</ModalBody>
          <ModalFooter>
            <Button colorScheme="red" mr={3} onClick={confirmDelete}>
              Delete
            </Button>
            <Button variant="ghost" onClick={() => setIsOpen(false)}>
              Cancel
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>

      {/* Color Overview Component */}
      <ColorOverview themeColors={customTheme.colors.brand} />
    </ChakraProvider>
  );
}

export default App;
